import React, { Component } from 'react';
import { BrowserRouter, Route, Link } from 'react-router-dom';

import Home from './Home/Home';
import Register from './Register/Register';
import Login from './Login/Login';
import Create from './Create/Create';
import Header from './Header/Header';
import './App.css';

class App extends Component {
  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleSubmit(e, data, isSignup) {
    e.preventDefault();
    fetch('http://localhost:9999/auth/sign' + (isSignup), {
      method: 'POST',
      body: JSON.stringify(data),
      headers: { 'Content-Type': 'application/json' }
    })
  }

  render() {
    return (
      <div className="App">
        { /* TODO */}
        <Header />
        <Route path="/" component={Home} />
        <Route render={() =>
          <Register handleSubmit={this.handleSubmit} handleChange={this.handleChange} />} path="/register" />
        <Route render={() =>
          <Login handleSubmit={this.handleSubmit} handleChange={this.handleChange} />} path="/login" />
      </div>
    );
  }
}

export default App;
