import React, { Component } from 'react';
import { BrowserRouter, Route, Link } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Home from './Home/Home';
import Register from './Register/Register';
import Login from './Login/Login';
import Create from './Create/Create';
import Header from './Header/Header';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: null
    }
  }

  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleSubmit(e, data, isSignup) {
    e.preventDefault();
    fetch('http://localhost:9999/auth/sign' + (isSignup ? 'up' : 'in'), {
      method: 'POST',
      body: JSON.stringify(data),
      headers: { 'Content-Type': 'application/json' }
    }).then(rawData => rawData.json())
      .then(responseBody => {
        if (responseBody.username) {
          this.setState({
            username: responseBody.username
          });
          toast.success(`Welcome, ${responseBody.username}`)
        }
      })
  }

  render() {
    return (
      <div className="App">
        { /* TODO */}
        <Header username={this.state.username} />
        <Route path="/" component={Home} />
        <Route render={() =>
          <Register handleSubmit={this.handleSubmit.bind(this)} handleChange={this.handleChange} />} path="/register" />
        <Route render={() =>
          <Login handleSubmit={this.handleSubmit.bind(this)} handleChange={this.handleChange} />} path="/login" />
      </div>
    );
  }
}

export default App;
