import React, { Component } from 'react';
import { BrowserRouter, Route, Link, Redirect } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

import Home from './Home/Home';
import Register from './Register/Register';
import Login from './Login/Login';
import Create from './Create/Create';
import Header from './Header/Header';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: null,
      isAdmin: false
    }
  }

  componentWillMount(){
    const isAdmin = localStorage.getItem('isAdmin');
    if(localStorage.getItem('username')){
      this.setState({
        username: localStorage.getItem('username'),
        isAdmin: isAdmin
      })
    }
    fetch('')
  }

  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleCreateSubmit(e, data) {
    e.preventDefault();
    fetch('http://localhost:9999/feed/movie/create', {
      method: 'POST',
      body: JSON.stringify(data),
      headers: { 'Content-Type': 'application/json' }
    }).then(rawData => rawData.json())
      .then(responseBody => {
        if (!responseBody.errors) {
          toast.success(responseBody.message, {
            closeButton: false
          })
        } else {
          toast.error(responseBody.message, {
            closeButton: false
          })
        }
      })
  }


  handleSubmit(e, data, isSignup) {
    e.preventDefault();
    fetch('http://localhost:9999/auth/sign' + (isSignup ? 'up' : 'in'), {
      method: 'POST',
      body: JSON.stringify(data),
      headers: { 'Content-Type': 'application/json' }
    }).then(rawData => rawData.json())
      .then(responseBody => {
        if (responseBody.username) {
          this.setState({
            username: responseBody.username,
            isAdmin: responseBody.isAdmin
          });
          localStorage.setItem('username', responseBody.username);
          localStorage.setItem('isAdmin', responseBody.isAdmin);
          toast.success(`Welcome, ${responseBody.username}`, {
            closeButton: false
          })
        } else {
          toast.error(responseBody.message, {
            closeButton: false
          })
        }

      })
  }

  render() {
    return (
      <div className="App">
        { /* TODO */}
        <ToastContainer />
        <Header isAdmin={this.state.isAdmin} username={this.state.username} />
        <Route path="/" component={Home} />
        <Route render={() =>
          this.state.isAdmin ?
            <Create
              handleCreateSubmit={this.handleCreateSubmit.bind(this)}
              handleChange={this.handleChange}
            /> :
            <Redirect
              to={{
                pathname: "/login"
              }}
            />
        }
          path="/create" />
        <Route render={() =>
          <Register handleSubmit={this.handleSubmit.bind(this)} handleChange={this.handleChange} />} path="/register" />
        <Route render={() =>
          <Login handleSubmit={this.handleSubmit.bind(this)} handleChange={this.handleChange} />} path="/login" />
      </div>
    );
  }
}

export default App;
